css:
/* Page setup */
@page { size: A4; margin: 0; }
html,body{height:100%;margin:0;padding:0;box-sizing:border-box}
body {
  font-family: "Arial", Helvetica, sans-serif;
  font-size: 11px;
  color: #111;
  -webkit-print-color-adjust: exact;
  background: #fff;
}

/* Outer wrapper that mimics the scanned border */
.po-page {
  width: 190mm;
  min-height: 297mm;
  margin: 0 auto;
  box-sizing: border-box;
  /* Optional faint background of scanned reference — remove if not needed */
  /* background-image: url('/mnt/data/Screenshot from 2025-11-25 11-17-26.png'); */
  /* background-repeat: no-repeat; background-position: center top; background-size: cover; */
}

/* Thick outer visible border like the scan */
.po-border {
  border: 2px solid #222;
  /*padding: 6px;*/
  height: calc(100% - 12px);
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
}



/* Header table: logo left, company center */
.header-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 6px;
}
.header-table td { vertical-align: top; }
.header-table .logo {
  width: 16%;
}
.header-table .logo img { width: 100px; height: auto; display:block; }
.header-table .company {
  width: 84%;
  text-align: center;
}
.company .title {
  font-size: 18px;
  font-weight: 700;
  letter-spacing: 0.6px;
}
.company .addr {
  font-size: 10px;
  line-height: 1.1;
  margin-top: 3px;
}

/* Purchase Order title block */
.po-title {
  text-align: center;
  font-size: 16px;
  font-weight: 700;
  margin: 6px 0;
  border-top: 1px solid #111;
  padding-top: 4px;
}



/* PO meta row */
.po-meta {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 10px;
}
.po-meta td {
  border: 1px solid #000;
  padding: 5px 8px;
  font-size: 10px;
  text-align: center;
  border-left: none;
}

/* Two-column vendor/delivery */
.two-col, .three-col {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 6px;
}

.two-col th,
.two-col td {
    width: 50%;     /* both columns always equal */
}


.ref-doc {
  font-size: 10px;
  margin-bottom: 6px;
}

.two-col th, .two-col td,
.three-col th, .three-col td {
  border: 1px solid #000;
  padding: 6px;
  font-size: 10px;
  vertical-align: top;
}
.two-col th, .three-col th {
  background: #f5f5f5;
  font-weight: 700;
}

/* Items table */
.item-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 6px;
  table-layout: fixed;
  font-size: 10px;
}
.item-table thead th {
  border: 1px solid #000;
  padding: 6px;
  background: #f4f4f4;
  text-align: center;
  font-weight: 700;
}
.item-table tbody td {
  border: 1px solid #000;
  padding: 6px;
  vertical-align: middle;
  word-wrap: break-word;
  text-align: left;
}
.item-table td.center { text-align: center; }
.item-table td.right { text-align: right; }

/* Annex line centered */
.annex {
  text-align: center;
  margin: 6px 0;
  font-size: 10px;
}

/* Big bottom block: left terms box + right totals box
   We'll create two columns with a single thin surrounding border
*/
/* Container for both columns */
.bottom-wrap {
  display: flex;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;  /* full bottom border */
  padding-bottom: 15px;
  margin-bottom: 15px;           /* space before other T&C */
}

/* LEFT SECTION */
.terms-box {
  width: 60%;
  padding: 10px;
  border-right: 1px solid #000;  /* shared middle border */
  font-size: 12px;
  line-height: 1.4;
}

/* GST Notification — full width & aligned to GST row */
.gst-note {
  padding: 6px 0;
  margin: 12px 0;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
  font-weight: bold;
}

/* Extra spacing before second set of terms */
.terms-after-space {
  margin-top: 12px;
}

/* RIGHT SECTION */
.totals-box {
  width: 40%;
  padding: 10px;
}

.totals-box table {
  width: 100%;
  border-collapse: collapse;
}

.totals-box td {
  padding: 6px 0;
}

.right {
  text-align: right;
}

/* GST row styling to match GST note */
.gst-row td {
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
  font-weight: bold;
  padding: 6px 0;
}

.grand td {
  font-weight: bold;
  border-top: 1px solid #000;
  padding-top: 6px;
}



/* Below that, the "Other Terms and Conditions" multi-row table and attached docs */
.terms-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 6px;
  font-size: 10px;
}
.terms-table td, .terms-table th {
  border: 1px solid #000;
  padding: 6px;
  vertical-align: top;
}

/* Attached docs rows */
.attach-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 6px;
  font-size: 10px;
}
.attach-table td, .attach-table th {
  border: 1px solid #000;
  padding: 6px;
}

/* Signature area — two columns inside an outer bordered row */
.sign-row {
  width: 100%;
  border-collapse: collapse;
  margin-top: 6px;
  font-size: 10px;
}
.sign-row td {
  border: 1px solid #000;
  padding: 8px;
  vertical-align: top;
}
.sign-left { width: 66%; }
.sign-right { width: 34%; text-align:left; }

/* signature line style inside sign-right */
.sign-right .sig-line, .sign-left .sig-line {
  width: 85%;
  border-top: 1px solid #000;
  margin-top: 18px;
}

/* small helper */
.right { text-align: right; }
.center { text-align: center; }

/* print friendly fix */
@media print {
  .po-page { width: auto; }
  .po-border { margin: 0; }
}



.bottom-table {
  width: 80%;
  border-collapse: collapse;
  margin-top: 10px;
  margin-bottom: 10px;
  border-bottom: 1px solid #000;
  margin-right: 0;
  margin-left: auto;
}

.bottom-table td {
  vertical-align: top;
  border: 1px solid #000;
  /*padding: 8px;*/
  font-size: 10px;

}

.print-format td,
.print-format th{
    padding: 0 !important;
}

.left-col { width: 60%; }
.right-col { width: 40%; }




/* Inner totals table */
.inner-tbl {
  width: 100%;
  border-collapse: collapse;
  border: none;
}

/* General cell styling */
.inner-tbl td {
  padding: 4px 0;
  border: none;
  font-size: 12px;
}

/* Add bottom border for every row */
.inner-tbl tr td {
  border-bottom: 1px solid #000;
}

/* Remove bottom border from last row */
.inner-tbl tr:last-child td {
  border-bottom: none;
}

/* Continuous vertical border between the two columns */
.inner-tbl td:first-child {
  padding-right: 6px;
}

/* Right aligned numeric column */
.inner-tbl td.right {
  text-align: right;
}

/* GST + Grand Total bold */
.gst-highlight td,
.grand td {
  font-weight: bold;
}
